(function() {
   // your page initialization code here
   // the DOM will be available here  
var aTags = document.querySelectorAll('span[href]');
var elems = document.getElementsByTagName('body')[0];
var target = elems.innerHTML;
elems.innerHTML = target.replace(/(<a)/igm, '<span').replace(/<\/a>/igm, '</span>');

for(var i = 0; i < aTags.length; i++){
    var aTag = aTags[i];
    aTag.addEventListener('click', function(e){
        var ele = e.target;
        window.location.replace(ele.getAttribute('href'));
    });
}
})();
